import React, { useEffect, useState } from 'react';
import yaml from 'js-yaml';
import './App.css';

interface TagValue {
  name?: string;
  capacities?: string[];
  manufacturer?: string;
}

interface TagData {
  type: string;
  description?: string;
  values?: Record<string, string | TagValue>;
  subcategories?: Record<string, {
    description?: string;
    values: Record<string, string | TagValue>;
  }>;
}

function App() {
  const [tagsData, setTagsData] = useState<Record<string, TagData>>({});
  const [selectedTags, setSelectedTags] = useState<Set<string>>(new Set());
  const [selectedCategory, setSelectedCategory] = useState<string>('');
  const [selectedSubcategory, setSelectedSubcategory] = useState<string>('');

  useEffect(() => {
    // Cambiar la ruta para apuntar al archivo correcto
    fetch('/tags.yml')
      .then(response => response.text())
      .then(text => {
        const data = yaml.load(text) as Record<string, TagData>;
        console.log('Tags cargados:', data);
        setTagsData(data);
      })
      .catch(error => {
        console.error('Error cargando tags.yml:', error);
        alert('Error al cargar las categorías');
      });
  }, []);

  const handleCategoryChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const category = event.target.value;
    setSelectedCategory(category);
    setSelectedSubcategory('');
  };

  const handleSubcategoryChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedSubcategory(event.target.value);
  };

  const getValueDisplay = (value: string | TagValue): string => {
    if (typeof value === 'string') return value;
    if (value.name) return value.name;
    return 'Sin descripción';
  };

  const handleValueSelect = (value: string) => {
    let tag: string;

    if (currentCategory?.type === 'simple') {
      tag = `${selectedCategory}#${value}`;
    } else if (currentCategory?.type === 'nested' && selectedSubcategory) {
      // Construir tag según la estructura definida en tag_structure
      tag = `${selectedCategory}#${selectedSubcategory}:${value}`;
    } else {
      return;
    }

    // Prevenir duplicados
    if (!selectedTags.has(tag)) {
      setSelectedTags(prev => new Set([...prev, tag]));
    }

    // Resetear selecciones
    setSelectedCategory('');
    setSelectedSubcategory('');
  };

  const removeTag = (tag: string) => {
    setSelectedTags(prev => {
      const next = new Set(prev);
      next.delete(tag);
      return next;
    });
  };

  const copyToClipboard = () => {
    const text = Array.from(selectedTags).sort().join('\n');
    navigator.clipboard.writeText(text);
    alert('Tags copiados al portapapeles!');
  };

  const mainCategories = Object.entries(tagsData)
    .filter(([category]) => 
      // Filtrar solo las categorías principales válidas
      category !== 'tag_structure' && 
      category !== 'search' &&
      (tagsData[category].type === 'simple' || tagsData[category].type === 'nested')
    )
    .map(([category, data]) => ({
      id: category,
      name: getCategoryDisplayName(category, data)
    }))
    .sort((a, b) => a.name.localeCompare(b.name));

  function getCategoryDisplayName(category: string, data: TagData): string {
    // Usar description si existe, sino formatear el nombre de la categoría
    return data.description || 
           category.split('_')
                  .map(word => word.charAt(0).toUpperCase() + word.slice(1))
                  .join(' ');
  }

  const currentCategory = selectedCategory ? tagsData[selectedCategory] : null;

  return (
    <div className="app">
      <h1>Generador de Tags</h1>

      <div className="category-section">
        <h3>Seleccionar Tags</h3>
        <div className="dropdown-group">
          <select value={selectedCategory} onChange={handleCategoryChange}>
            <option value="">Seleccionar Categoría...</option>
            {mainCategories.map(category => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>

          {currentCategory?.type === 'nested' && (
            <select value={selectedSubcategory} onChange={handleSubcategoryChange}>
              <option value="">Seleccionar Subcategoría...</option>
              {Object.entries(currentCategory.subcategories || {}).map(([subcat, data]) => (
                <option key={subcat} value={subcat}>
                  {data.description || subcat.replace(/_/g, ' ')}
                </option>
              ))}
            </select>
          )}

          <select 
            onChange={(e) => handleValueSelect(e.target.value)}
            value=""
            disabled={!selectedCategory || (currentCategory?.type === 'nested' && !selectedSubcategory)}
          >
            <option value="">Seleccionar Valor...</option>
            {currentCategory?.type === 'simple' && currentCategory.values && 
              Object.entries(currentCategory.values)
                .sort(([a], [b]) => a.localeCompare(b))
                .map(([key, value]) => (
                  <option key={key} value={key}>
                    {getValueDisplay(value)}
                  </option>
                ))
            }
            {currentCategory?.type === 'nested' && selectedSubcategory && 
              Object.entries(currentCategory.subcategories?.[selectedSubcategory].values || {})
                .sort(([a], [b]) => a.localeCompare(b))
                .map(([key, value]) => (
                  <option key={key} value={key}>
                    {getValueDisplay(value)}
                  </option>
                ))
            }
          </select>
        </div>
      </div>

      <div className="selected-tags">
        <h3>Tags Seleccionados:</h3>
        <div className="tags-list">
          {Array.from(selectedTags).sort().map(tag => (
            <span key={tag} className="tag">
              {tag}
              <button onClick={() => removeTag(tag)} title="Eliminar">&times;</button>
            </span>
          ))}
        </div>
      </div>

      <div className="output-section">
        <textarea
          readOnly
          value={Array.from(selectedTags).sort().join('\n')}
          placeholder="Los tags seleccionados aparecerán aquí..."
        />
        <button className="copy-btn" onClick={copyToClipboard}>
          Copiar Tags
        </button>
      </div>
    </div>
  );
}

export default App;
